static std::string lexStringLiteral(kasm::CompoundInputFileStream& in)
{
	std::string str;

	std::streampos mar;
	for (;;)
	{
		
{
	char yych;
	yych = in.peek();
	switch (yych) {
	case '\n':	goto yy4;
	case '"':	goto yy6;
	case '\\':	goto yy8;
	default:	goto yy2;
	}
yy2:
	do { in.ignore(); if (in.eof()) throw std::exception("Unclosed string"); } while(0);
	{ str.push_back(yych); continue; }
yy4:
	do { in.ignore(); if (in.eof()) throw std::exception("Unclosed string"); } while(0);
	{ throw std::exception("Unclosed string"); }
yy6:
	do { in.ignore(); if (in.eof()) throw std::exception("Unclosed string"); } while(0);
	{ break; }
yy8:
	do { in.ignore(); if (in.eof()) throw std::exception("Unclosed string"); } while(0);
	yych = in.peek();
	switch (yych) {
	case '"':
	case '\'':
	case '\\':
	case 'a':
	case 'b':
	case 'f':
	case 'n':
	case 'r':
	case 't':
	case 'v':	goto yy10;
	default:	goto yy9;
	}
yy9:
	{ throw std::exception("Illegal escape character in string"); }
yy10:
	do { in.ignore(); if (in.eof()) throw std::exception("Unclosed string"); } while(0);
	{ str.push_back('\\'); str.push_back(yych); continue; }
}

	}

	return str;
}

static std::string blockAsString(kasm::CompoundInputFileStream& in)
{
	std::string str;
	
	char c;
	for (;;)
	{
		in.get(c);
		if (c == '}') break;
		str.push_back(c);
	}

	return str;
}

static std::string getString(kasm::CompoundInputFileStream& in, std::streampos start, std::streampos end)
{
	std::string buffer;
	buffer.resize(end - start);
	in.seekg(start);
	in.read(buffer.data(), end - start);
	return buffer;
}

static char getChar(kasm::CompoundInputFileStream& in, std::streampos start, std::streampos end)
{
	in.seekg(start);
	char c;
	in.get(c);
	in.seekg(end);
	return c;
}

